actions.script:
const historyCids = event.data.historyCids;
historyCids.pop();
const cid = historyCids[historyCids.length - 1];
doAction({ actionType: 'reload', componentId: 'RootPage', data: { attachment_classify_id: cid }, dataMergeMode: 'merge' })

const data = event.data;
const iids = data.url ? data.selectedAids : data.selectedCids;
if (data.value) {
  iids.push(data.id);
} else {
  const index = iids.indexOf(data.id);
  if (index != -1) {
    iids.splice(index, 1);
  }
}
if (data.url) {
  doAction({ actionType: 'setValue', componentId: 'RootPage', args: { selectedAids: iids } })
} else {
  doAction({ actionType: 'setValue', componentId: 'RootPage', args: { selectedCids: iids } })
}

let lastClick = event.data.lastClick || 0;
if (Date.now() - lastClick < 300) {
      doAction({
            actionType: 'reload',
            componentId: 'RootPage',
            data: {
                  attachment_classify_id: event.data.id
            },
            dataMergeMode: 'merge'
      });
}
event.data.lastClick = Date.now();

const attachment_classify_id = event.data.attachment_classify_id;
const historyCids = event.data.historyCids
if (historyCids.length > 0) {
  if (historyCids[historyCids.length - 1] != attachment_classify_id) {
    historyCids.push(attachment_classify_id);
  }
} else {
  historyCids.push(attachment_classify_id);
}
